import * as React from "react";
import Subhead from "../Typography/Subhead/Subhead";
import { getClassName } from "../../helpers/getClassName";
import { usePlatform } from "../../hooks/usePlatform";
import Headline from "../Typography/Headline/Headline";
import { hasReactNode } from "../../lib/utils";
import "./InfoRow.css";

export interface InfoRowProps extends React.HTMLAttributes<HTMLDivElement> {
  header: React.ReactNode;
}

const InfoRow: React.FunctionComponent<InfoRowProps> = ({
  header,
  children,
  ...restProps
}: InfoRowProps) => {
  const platform = usePlatform();

  return (
    <Headline
      {...restProps}
      vkuiClass={getClassName("InfoRow", platform)}
      weight="regular"
    >
      {hasReactNode(header) && (
        <Subhead Component="span" vkuiClass="InfoRow__header">
          {header}
        </Subhead>
      )}
      {children}
    </Headline>
  );
};

// eslint-disable-next-line import/no-default-export
export default InfoRow;
