import { bps, Milliseconds } from '@vkontakte/videoplayer-shared';
export interface IRepresentation {
    width: number;
    height: number;
    bandwidth: bps;
    baseURL: string;
    failoverHosts?: string[];
    indexFrom: number;
    indexTo: number;
    initFrom: number;
    initTo: number;
    codecs?: string;
    mimeType?: string;
    fps?: number;
    bufferSize: number;
    bufferLength: number;
    name?: string;
    id?: string;
    refs?: IRef[];
    initMessage?: Uint8Array;
}
export interface IRef {
    fromTime: number;
    toTime: number;
    fromOffset: number;
    toOffset: number;
}
export interface IDashConfig {
    FRONT_CACHE_DURATION: number;
    SEEK_IN_SEGMENT_THRESHOLD: Milliseconds;
    SEEK_IN_SEGMENT_DELTA: Milliseconds;
    REQUEST_QUIC: boolean;
}
